import React, { useMemo, useState } from "react";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";

// -------------------- Config --------------------
const OCCUPANCY_OPTIONS = ["Retail", "Restaurant", "Administrative", "Mechanical"];
const OCCUPANCY_FACTORS = { Retail: 2.8, Restaurant: 1.4, Administrative: 9.3, Mechanical: 28 };
const HEADERS = ["Room #", "Room Name", "Area (m²)", "Occupancy Type", "Occupant Load"];

// -------------------- Helpers --------------------
const normalizeType = (t) => (OCCUPANCY_OPTIONS.includes((t || "").trim()) ? (t || "").trim() : "Retail");
const loadFrom = (area, type) => {
  const a = Number(area);
  const f = OCCUPANCY_FACTORS[type] ?? OCCUPANCY_FACTORS.Retail;
  return Number.isFinite(a) && a > 0 ? Math.ceil(a / f) : 0;
};

// -------------------- Component --------------------
export default function OccuCalcTools() {
  const [mode, setMode] = useState("manual"); // "manual" | "upload"

  // Manual rows live in a compact shape
  const [manualRows, setManualRows] = useState([
    { id: 1, number: "1", name: "Space 1", area: "", type: "Retail" },
  ]);

  // Uploaded/edited grid rows (header-style keys)
  const [gridRows, setGridRows] = useState([]);

  // Totals (derived from whichever set is active)
  const { totals, grandTotal } = useMemo(() => {
    const rows =
      mode === "manual"
        ? manualRows.map((r) => ({
          "Room #": r.number,
          "Room Name": r.name,
          "Area (m²)": r.area,
          "Occupancy Type": r.type,
          "Occupant Load": loadFrom(r.area, r.type),
        }))
        : gridRows;

    const grouped = {};
    let sum = 0;
    rows.forEach((r) => {
      const t = normalizeType(r["Occupancy Type"]);
      const l = Number(r["Occupant Load"]) || 0;
      grouped[t] = (grouped[t] || 0) + l;
      sum += l;
    });
    return { totals: grouped, grandTotal: sum };
  }, [mode, manualRows, gridRows]);

  // -------------------- Handlers --------------------
  const handleModeChange = (e) => {
    setMode(e.target.value);
  };

  // Manual CRUD
  const addManualRow = () =>
    setManualRows((prev) => {
      const id = prev.length ? Math.max(...prev.map((x) => x.id)) + 1 : 1;
      return [...prev, { id, number: String(id), name: `Space ${id}`, area: "", type: "Retail" }];
    });

  const removeManualRow = (id) => setManualRows((prev) => prev.filter((r) => r.id !== id));

  const changeManual = (id, key, val) =>
    setManualRows((prev) =>
      prev.map((r) => (r.id === id ? { ...r, [key]: key === "type" ? normalizeType(val) : val } : r))
    );

  const clearManual = () =>
    setManualRows([{ id: 1, number: "1", name: "Space 1", area: "", type: "Retail" }]);

  // Upload
  const handleUpload = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (evt) => {
      const wb = XLSX.read(evt.target.result, { type: "binary" });
      const ws = wb.Sheets[wb.SheetNames[0]];
      const rows = XLSX.utils.sheet_to_json(ws, { defval: "" }).map((row, i) => {
        const type = normalizeType(row["Occupancy Type"] ?? row["Type"]);
        const area = row["Area (m²)"] ?? row["Area"] ?? "";
        return {
          id: i + 1,
          "Room #": String(row["Room #"] ?? row["Room Number"] ?? i + 1),
          "Room Name": String(row["Room Name"] ?? row["Name"] ?? `Space ${i + 1}`),
          "Area (m²)": area,
          "Occupancy Type": type,
          "Occupant Load": loadFrom(area, type),
        };
      });
      setGridRows(rows);
    };
    reader.readAsBinaryString(file);
    e.target.value = null;
  };

  const changeGrid = (id, key, val) =>
    setGridRows((prev) =>
      prev.map((r) => {
        if (r.id !== id) return r;
        const next = { ...r, [key]: key === "Occupancy Type" ? normalizeType(val) : val };
        next["Occupancy Type"] = normalizeType(next["Occupancy Type"]);
        next["Occupant Load"] = loadFrom(next["Area (m²)"], next["Occupancy Type"]);
        return next;
      })
    );

  const addGridRow = () =>
    setGridRows((prev) => {
      const id = prev.length ? Math.max(...prev.map((x) => x.id)) + 1 : 1;
      return [
        ...prev,
        {
          id,
          "Room #": String(id),
          "Room Name": `Space ${id}`,
          "Area (m²)": "",
          "Occupancy Type": "Retail",
          "Occupant Load": 0,
        },
      ];
    });

  const removeGridRow = (id) => setGridRows((prev) => prev.filter((r) => r.id !== id));
  const clearGrid = () => setGridRows([]);

  // Exports
  const exportExcel = () => {
    const rows =
      mode === "manual"
        ? manualRows.map((r) => ({
          "Room #": r.number,
          "Room Name": r.name,
          "Area (m²)": r.area,
          "Occupancy Type": r.type,
          "Occupant Load": loadFrom(r.area, r.type),
        }))
        : gridRows;

    const ws = XLSX.utils.json_to_sheet(rows, { header: HEADERS });
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Occupancy");
    const buf = XLSX.write(wb, { type: "array", bookType: "xlsx" });
    saveAs(new Blob([buf], { type: "application/octet-stream" }), "occupancy_data.xlsx");
  };

  const exportPDFSummary = () => {
    const doc = new jsPDF({ unit: "pt", format: "a4" });
    let y = 64;
    doc.setFontSize(18);
    doc.text("Occupancy Summary", 40, y);
    y += 24;

    doc.setFontSize(12);
    Object.entries(totals).forEach(([type, count]) => {
      doc.text(`${type}: ${count} occupants`, 40, y);
      y += 16;
    });

    y += 10;
    doc.setFontSize(14);
    doc.text(`Grand Total: ${grandTotal} occupants`, 40, y);
    doc.save("occupancy_summary.pdf");
  };

  const exportPDFDetailed = () => {
    const rows =
      mode === "manual"
        ? manualRows.map((r) => ({
          "Room #": r.number,
          "Room Name": r.name,
          "Area (m²)": r.area,
          "Occupancy Type": r.type,
          "Occupant Load": loadFrom(r.area, r.type),
        }))
        : gridRows;

    const doc = new jsPDF({ unit: "pt", format: "a4" });
    let y = 64;
    doc.setFontSize(18);
    doc.text("Occupancy Detailed Report", 40, y);
    y += 24;

    doc.setFontSize(10);
    doc.text("Room #", 40, y);
    doc.text("Room Name", 110, y);
    doc.text("Area (m²)", 280, y);
    doc.text("Type", 360, y);
    doc.text("Load", 460, y);
    y += 12;
    doc.line(40, y, 520, y);
    y += 12;

    rows.forEach((r, i) => {
      if (y > 760) {
        doc.addPage();
        y = 64;
      }
      doc.text(String(r["Room #"]), 40, y);
      doc.text(String(r["Room Name"]), 110, y);
      doc.text(String(r["Area (m²)"]), 280, y);
      doc.text(String(r["Occupancy Type"]), 360, y);
      doc.text(String(r["Occupant Load"]), 460, y);
      y += 14;
    });

    y += 12;
    doc.setFontSize(12);
    doc.text(`Grand Total: ${grandTotal} occupants`, 40, y);
    doc.save("occupancy_detailed.pdf");
  };

  const downloadTemplate = () => {
    const example = [
      { "Room #": "1", "Room Name": "Open Office", "Area (m²)": 186, "Occupancy Type": "Administrative" },
      { "Room #": "2", "Room Name": "Retail Hall", "Area (m²)": 140, "Occupancy Type": "Retail" },
      { "Room #": "3", "Room Name": "Kitchen", "Area (m²)": 56, "Occupancy Type": "Restaurant" },
    ];
    const ws = XLSX.utils.json_to_sheet(example, {
      header: ["Room #", "Room Name", "Area (m²)", "Occupancy Type"],
    });
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Template");
    const buf = XLSX.write(wb, { type: "array", bookType: "xlsx" });
    saveAs(new Blob([buf], { type: "application/octet-stream" }), "OccuCalc_template.xlsx");
  };

  // -------------------- UI --------------------
  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 24, maxWidth: 1100, margin: "0 auto" }}>
      <h1 style={{ margin: 0 }}>🏢 OccuCalc</h1>
      <p style={{ color: "#444", marginTop: 8 }}>
        Calculate occupant loads manually or from Excel, then export a summary.
      </p>

      {/* Controls */}
      <div
        style={{
          display: "flex",
          gap: 12,
          alignItems: "center",
          border: "1px solid #e5e7eb",
          borderRadius: 10,
          padding: 12,
          marginTop: 12,
        }}
      >
        <label><strong>Mode:</strong></label>
        <select value={mode} onChange={handleModeChange} style={{ padding: "6px 10px", borderRadius: 8 }}>
          <option value="manual">Manual entry</option>
          <option value="upload">Upload Excel</option>
        </select>

        {mode === "upload" && (
          <>
            <input type="file" accept=".xlsx,.xls" onChange={handleUpload} style={{ marginLeft: 8 }} />
            <button onClick={downloadTemplate} style={btn()}>
              Download template
            </button>
          </>
        )}

        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <button onClick={exportExcel} style={btn()}>Export Excel</button>
          <button onClick={exportPDFSummary} style={btn()}>PDF Summary</button>
          <button onClick={exportPDFDetailed} style={btn()}>PDF Detailed</button>
        </div>
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 320px", gap: 16, marginTop: 16 }}>
        {/* Left: Tables */}
        <div>
          {mode === "manual" ? (
            <ManualTable
              rows={manualRows}
              onAdd={addManualRow}
              onRemove={removeManualRow}
              onChange={changeManual}
              onClear={clearManual}
            />
          ) : (
            <GridTable
              rows={gridRows}
              onAdd={addGridRow}
              onRemove={removeGridRow}
              onChange={changeGrid}
              onClear={clearGrid}
            />
          )}
        </div>

        {/* Right: Totals */}
        <aside style={{ border: "1px solid #e5e7eb", borderRadius: 10, padding: 16, height: "fit-content" }}>
          <h3 style={{ marginTop: 0 }}>Totals</h3>
          <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
            {OCCUPANCY_OPTIONS.map((t) => (
              <li key={t} style={{ display: "flex", justifyContent: "space-between", padding: "6px 0" }}>
                <span>{t}</span>
                <strong>{totals[t] || 0}</strong>
              </li>
            ))}
          </ul>
          <hr style={{ margin: "12px 0" }} />
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <span>Grand Total</span>
            <strong>{grandTotal}</strong>
          </div>
        </aside>
      </div>
    </div>
  );
}

// -------------------- Sub-components --------------------
function ManualTable({ rows, onAdd, onRemove, onChange, onClear }) {
  return (
    <div style={{ border: "1px solid #e5e7eb", borderRadius: 10, overflow: "hidden" }}>
      <div style={{ padding: 12, display: "flex", gap: 8, alignItems: "center", background: "#fafafa" }}>
        <strong>Manual Entry</strong>
        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <button onClick={onAdd} style={btn()}>Add row</button>
          <button onClick={onClear} style={btn("ghost")}>Clear</button>
        </div>
      </div>
      <div style={{ overflowX: "auto" }}>
        <table style={table()}>
          <thead>
            <tr>
              <th style={th(80)}>#</th>
              <th style={th()}>Room Name</th>
              <th style={th(120)}>Area (m²)</th>
              <th style={th(160)}>Occupancy Type</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r) => (
              <tr key={r.id}>
                <td style={td(80)}>
                  <input value={r.number} onChange={(e) => onChange(r.id, "number", e.target.value)} style={input(70)} />
                </td>
                <td style={td()}>
                  <input value={r.name} onChange={(e) => onChange(r.id, "name", e.target.value)} style={input()} />
                </td>
                <td style={td(120)}>
                  <input
                    value={r.area}
                    onChange={(e) => onChange(r.id, "area", e.target.value)}
                    style={input(100)}
                    inputMode="decimal"
                    placeholder="0"
                  />
                </td>
                <td style={td(160)}>
                  <select
                    value={r.type}
                    onChange={(e) => onChange(r.id, "type", e.target.value)}
                    style={select(150)}
                  >
                    {OCCUPANCY_OPTIONS.map((opt) => (
                      <option key={opt} value={opt}>{opt}</option>
                    ))}
                  </select>
                </td>
              </tr>
            ))}
            {rows.length === 0 && (
              <tr>
                <td colSpan={4} style={{ padding: 16, textAlign: "center", color: "#666" }}>
                  No rows. Click <em>Add row</em> to start.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function GridTable({ rows, onAdd, onRemove, onChange, onClear }) {
  return (
    <div style={{ border: "1px solid #e5e7eb", borderRadius: 10, overflow: "hidden" }}>
      <div style={{ padding: 12, display: "flex", gap: 8, alignItems: "center", background: "#fafafa" }}>
        <strong>Uploaded / Editable Grid</strong>
        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <button onClick={onAdd} style={btn()}>Add row</button>
          <button onClick={onClear} style={btn("ghost")}>Clear</button>
        </div>
      </div>
      <div style={{ overflowX: "auto" }}>
        <table style={table()}>
          <thead>
            <tr>
              <th style={th(80)}>Room #</th>
              <th style={th()}>Room Name</th>
              <th style={th(120)}>Area (m²)</th>
              <th style={th(160)}>Occupancy Type</th>
              <th style={th(80)}>Load</th>
              <th style={th(40)}></th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r) => (
              <tr key={r.id}>
                <td style={td(80)}>
                  <input value={r["Room #"]} onChange={(e) => onChange(r.id, "Room #", e.target.value)} style={input(70)} />
                </td>
                <td style={td()}>
                  <input value={r["Room Name"]} onChange={(e) => onChange(r.id, "Room Name", e.target.value)} style={input()} />
                </td>
                <td style={td(120)}>
                  <input
                    value={r["Area (m²)"]}
                    onChange={(e) => onChange(r.id, "Area (m²)", e.target.value)}
                    style={input(100)}
                    inputMode="decimal"
                    placeholder="0"
                  />
                </td>
                <td style={td(160)}>
                  <select
                    value={r["Occupancy Type"]}
                    onChange={(e) => onChange(r.id, "Occupancy Type", e.target.value)}
                    style={select(150)}
                  >
                    {OCCUPANCY_OPTIONS.map((opt) => (
                      <option key={opt} value={opt}>{opt}</option>
                    ))}
                  </select>
                </td>
                <td style={td(80)}>
                  <div style={pill()}>{r["Occupant Load"]}</div>
                </td>
                <td style={td(40)}>
                  <button onClick={() => onRemove(r.id)} style={btn("danger")}>×</button>
                </td>
              </tr>
            ))}
            {rows.length === 0 && (
              <tr>
                <td colSpan={6} style={{ padding: 16, textAlign: "center", color: "#666" }}>
                  Upload an Excel file or click <em>Add row</em> to begin.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      <div style={{ padding: 12 }}>
        <button onClick={downloadTemplate} style={btn()}>Download template</button>
      </div>
    </div>
  );
}

// -------------------- Tiny style helpers --------------------
function btn(variant = "primary") {
  const base =
    variant === "danger"
      ? { background: "#ef4444", color: "#fff", border: "1px solid #dc2626" }
      : variant === "ghost"
        ? { background: "#fff", color: "#111", border: "1px solid #e5e7eb" }
        : { background: "#2563eb", color: "#fff", border: "1px solid #1d4ed8" };
  return { ...base, borderRadius: 8, padding: "8px 12px", fontSize: 14, cursor: "pointer" };
}
const table = () => ({ width: "100%", borderCollapse: "separate", borderSpacing: 0 });
const th = (w) => ({ textAlign: "left", background: "#f8fafc", padding: "10px 12px", borderBottom: "1px solid #e5e7eb", width: w, whiteSpace: "nowrap", fontWeight: 700, fontSize: 13 });
const td = (w) => ({ padding: "8px 12px", borderBottom: "1px solid #f1f5f9", width: w, verticalAlign: "middle" });
const input = (w) => ({ width: w || "100%", padding: "6px 8px", borderRadius: 8, border: "1px solid #e5e7eb", fontSize: 14 });
const select = (w) => ({ width: w || "100%", padding: "6px 8px", borderRadius: 8, border: "1px solid #e5e7eb", fontSize: 14, background: "#fff" });
const pill = () => ({ display: "inline-block", padding: "4px 10px", borderRadius: 999, background: "#111827", color: "#fff", fontSize: 13, lineHeight: 1.4 });
